.form__switch {
  display: none;
}

.form__switch + .form__label {
  position: relative;
  display: inline-block;
  padding-bottom: 0;
  padding-left: 50px;
  cursor: pointer;
  text-align: left;
}

.form__switch + .form__label::before, .form__switch + .form__label::after {
  position: absolute;
  top: 50%;
  margin: 0;
  transform: translate(0, -50%);
  transition: all 0.3s ease;
  content: "";
  outline: 0;
}

.form__switch + .form__label::before {
  left: 2px;
  width: 34px;
  height: 14px;
  background-color: #f2f2f2;
  border-radius: 8px;
}

.form__switch + .form__label::after {
  left: 2px;
  width: 20px;
  height: 20px;
  background-color: #fff;
  border-radius: 50%;
  box-shadow: 0 3px 1px -2px rgba(0, 0, 0, 0.14), 0 2px 2px 0 rgba(0, 0, 0, 0.098), 0 1px 5px 0 rgba(0, 0, 0, 0.084);
}

.form__switch:checked + .form__label::before {
  background-color: currentColor;
  opacity: .5;
}

.form__switch:checked + .form__label::after {
  background-color: currentColor;
  transform: translate(80%, -50%);
}

.form__switch:disabled + .form__label::after {
  background-color: #fff;
}

.form__switch:disabled + .form__label::before {
  background-color: #efefef;
}


